Create the infrastructure implementation of EnvironmentGateway.

File: src/infrastructure/external/environment/SystemEnvironmentGateway.ts

Responsibilities:
- Filter process.env to exclude framework internals
- Provide system defaults (HOME, USER, SHELL, TERM)
- Validate environment variables for shell safety

Implementation:
export class SystemEnvironmentGateway implements EnvironmentGateway {
  getProcessEnvironment(): TmuxEnvironment {
    const env: Record<string, string> = {};
    for (const [key, value] of Object.entries(process.env)) {
      if (value === undefined) continue;
      // Filter framework internals
      if (key.startsWith('__NEXT_PRIVATE_')) continue;
      if (key.startsWith('__NEXT_ACTION_')) continue;
      if (key.startsWith('__VITE_')) continue;
      if (key.startsWith('__TURBOPACK_')) continue;
      env[key] = value;
    }
    return TmuxEnvironment.create(env);
  }

  getSystemDefaults(): TmuxEnvironment {
    return TmuxEnvironment.create({
      HOME: process.env.HOME || '/tmp',
      USER: process.env.USER || 'unknown',
      SHELL: process.env.SHELL || '/bin/bash',
      TERM: 'xterm-256color'
    });
  }

  validateForShell(vars: TmuxEnvironment): boolean {
    // Validate keys and values for shell safety
    for (const [key, value] of vars.entries()) {
      if (\!key.match(/^[A-Z][A-Z0-9_]*$/)) return false;
      // Add value validation as needed
    }
    return true;
  }
}

