Implement the new environment and hook methods in TmuxGatewayImpl.

File: src/infrastructure/external/tmux/TmuxGatewayImpl.ts

New Implementations:

async setEnvironment(sessionName: string, vars: TmuxEnvironment): Promise<void> {
  // Use tmux set-environment -t for each variable
  for (const [key, value] of vars.entries()) {
    await execFile('tmux', ['set-environment', '-t', sessionName, key, value]);
  }
}

async getEnvironment(sessionName: string): Promise<TmuxEnvironment> {
  const { stdout } = await execFile('tmux', ['show-environment', '-t', sessionName]);
  // Parse 'KEY=value' lines into Record
  const env: Record<string, string> = {};
  for (const line of stdout.split('\n')) {
    if (\!line.trim() || line.startsWith('-')) continue;
    const match = line.match(/^([^=]+)=(.*)$/);
    if (match) env[match[1]] = match[2];
  }
  return TmuxEnvironment.create(env);
}

async unsetEnvironment(sessionName: string, keys: string[]): Promise<void> {
  for (const key of keys) {
    await execFile('tmux', ['set-environment', '-t', sessionName, '-u', key]);
  }
}

async setHook(sessionName: string, hook: TmuxHook): Promise<void> {
  await execFile('tmux', ['set-hook', '-t', sessionName, hook.name, hook.command]);
}

async removeHook(sessionName: string, hookName: string): Promise<void> {
  await execFile('tmux', ['set-hook', '-t', sessionName, '-u', hookName]);
}

async setOption(sessionName: string, option: string, value: string): Promise<void> {
  await execFile('tmux', ['set-option', '-t', sessionName, option, value]);
}

async getOption(sessionName: string, option: string): Promise<string | null> {
  try {
    const { stdout } = await execFile('tmux', ['show-option', '-t', sessionName, '-v', option]);
    return stdout.trim();
  } catch {
    return null;
  }
}

