Enhance PortRegistryService with runtime port checking.

File: src/services/port-registry-service.ts

New Functions:

1. checkPortsInUse() - Check if ports are actually listening:
export async function checkPortsInUse(
  ports: number[]
): Promise<Array<{ port: number; inUse: boolean }>> {
  const results = await Promise.all(
    ports.map(async (port) => {
      try {
        // Use lsof to check if port is in use
        const { stdout } = await execFile('lsof', [
          '-i', ':${port}',
          '-sTCP:LISTEN',
          '-t'  // PIDs only
        ]);
        return { port, inUse: stdout.trim().length > 0 };
      } catch {
        return { port, inUse: false };  // lsof exits non-zero if nothing found
      }
    })
  );
  return results;
}

2. validatePortsRuntime() - Combined DB + runtime validation:
export async function validatePortsRuntime(
  folderId: string,
  userId: string,
  envVars: EnvironmentVariables | null
): Promise<PortValidationResult & { runtimeConflicts: number[] }> {
  const dbResult = await validatePorts(folderId, userId, envVars);

  if (\!envVars) return { ...dbResult, runtimeConflicts: [] };

  const ports = extractPortVariables(envVars).map(p => p.port);
  const runtimeChecks = await checkPortsInUse(ports);
  const runtimeConflicts = runtimeChecks.filter(c => c.inUse).map(c => c.port);

  return { ...dbResult, runtimeConflicts };
}

